import React, { useState } from 'react';
import Select from 'react-select';
import onboardingImage from '../assets/onboarding-image.jpg';
import { useNavigate } from 'react-router-dom';

const steps = [1, 2, 3, 4, 5];

const options = {
  goals: [
    { value: 'job', label: 'Find my First Job' },
    { value: 'interview', label: 'Practice Interviews' },
    { value: 'resume', label: 'Build My Resume' },
  ],
  education: [
    { value: 'highschool', label: 'High School' },
    { value: 'undergraduate', label: 'Undergraduate' },
    { value: 'postgraduate', label: 'Post Graduation' },
  ],
  fields: [
    { value: 'engineering', label: 'Engineering' },
    { value: 'arts', label: 'Arts' },
    { value: 'science', label: 'Science' },
  ],
  skills: [
    { value: 'python', label: 'Python' },
    { value: 'datascience', label: 'Data Science' },
    { value: 'django', label: 'Django' },
    { value: 'design', label: 'Product Design' },
  ],
  roles: [
    { value: 'developer', label: 'Software Developer' },
    { value: 'designer', label: 'Product Designer' },
  ],
  cities: [
    { value: 'boston', label: 'Boston' },
    { value: 'newyork', label: 'New York' },
    { value: 'seattle', label: 'Seattle' },
  ]
};

export default function Onboarding() {
  const [step, setStep] = useState(1);
  const [selectedGoals, setSelectedGoals] = useState([]);
  const [education, setEducation] = useState(null);
  const [field, setField] = useState(null);
  const [skills, setSkills] = useState([]);
  const [resume, setResume] = useState(null);
  const [roles, setRoles] = useState([]);
  const [cities, setCities] = useState([]);
  const [employmentType, setEmploymentType] = useState([]);
  const navigate = useNavigate();

  const handleToggle = (type) => {
    setEmploymentType((prev) =>
      prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type]
    );
  };

  const handleSubmit = () => {
    setStep(5);
    setTimeout(() => navigate('/recommended-jobs'), 2000);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white px-4 py-10">
      {step === 1 && (
        <div className="flex flex-col md:flex-row items-center justify-center max-w-6xl w-full">
          <div className="w-full md:w-1/2 text-center md:text-left space-y-6">
            <h1 className="text-4xl font-bold">Let’s Land Your First Job Faster with AI</h1>
            <p className="text-gray-500">Smart job matching. AI interviews. Instant resume tailoring.</p>
            <button onClick={() => setStep(2)} className="bg-purple-600 text-white px-5 py-2 rounded mt-4">Let’s Go!</button>
          </div>
          <div className="w-full md:w-1/2 mt-8 md:mt-0">
            <img src={onboardingImage} alt="Onboarding" className="rounded-lg shadow-lg" />
          </div>
        </div>
      )}

      {step === 2 && (
        <div className="w-full max-w-2xl text-center">
          <h1 className="text-3xl font-bold text-purple-600 mb-4">Launch<span className="text-black">Hire</span></h1>
          <h2 className="text-xl font-semibold mb-2">What’s Your First Step Toward Success?</h2>
          <p className="text-gray-600 mb-6">Whether you're job hunting, practicing interviews, or polishing your resume we’ll guide you every step.</p>
          <div className="flex justify-center gap-4 mb-6">
            {options.goals.map((goal) => (
              <button
                key={goal.value}
                onClick={() => {
                  setSelectedGoals((prev) =>
                    prev.includes(goal.value) ? prev.filter((g) => g !== goal.value) : [...prev, goal.value]
                  );
                }}
                className={`px-4 py-2 border rounded-full ${
                  selectedGoals.includes(goal.value) ? 'bg-purple-600 text-white' : 'bg-white text-black'
                }`}
              >
                {goal.label}
              </button>
            ))}
          </div>
          <div className="flex justify-center gap-4">
            <button onClick={() => setStep(1)} className="bg-gray-200 px-4 py-2 rounded">Back</button>
            <button onClick={() => setStep(3)} className="bg-purple-600 text-white px-4 py-2 rounded">Next</button>
          </div>
        </div>
      )}

      {step === 3 && (
        <div className="w-full max-w-xl">
          <h2 className="text-xl font-semibold mb-1">Help Us Know You Better.</h2>
          <p className="text-gray-500 mb-6">A few quick details so we can show you the most relevant jobs and best practice interviews.</p>

          <Select options={options.education} onChange={setEducation} placeholder="Education Status" className="mb-4" />
          <Select options={options.fields} onChange={setField} placeholder="Field of Study" className="mb-4" />
          <Select isMulti options={options.skills} onChange={setSkills} placeholder="Skills" className="mb-4" />

          <div className="border-2 border-dashed rounded-lg p-6 text-center text-gray-500 mb-6">
            <label className="cursor-pointer">
              <input type="file" accept="application/pdf" hidden onChange={(e) => setResume(e.target.files[0])} />
              <p className="font-semibold">Click to upload</p>
              <p className="text-xs">Only PDF is supported (Max : 2MB)</p>
            </label>
          </div>

          <div className="flex justify-between">
            <button onClick={() => setStep(2)} className="bg-gray-200 px-4 py-2 rounded">Back</button>
            <button onClick={() => setStep(4)} className="bg-purple-600 text-white px-4 py-2 rounded">Next</button>
          </div>
        </div>
      )}

      {step === 4 && (
        <div className="w-full max-w-xl">
          <h2 className="text-xl font-semibold mb-2">Dream Job? Let’s Find It Together.</h2>
          <p className="text-gray-500 mb-4">Choose the roles and cities you love, we’ll find the best matches for you.</p>

          <Select isMulti options={options.roles} onChange={setRoles} placeholder="Choose Preferred Job Roles" className="mb-4" />

          <div className="flex gap-3 mb-4">
            {['Remote', 'Hybrid', 'Onsite'].map((type) => (
              <button
                key={type}
                onClick={() => handleToggle(type)}
                className={`px-4 py-2 rounded-full border ${
                  employmentType.includes(type) ? 'bg-purple-600 text-white' : 'bg-white text-black'
                }`}
              >
                {type}
              </button>
            ))}
          </div>

          <Select isMulti options={options.cities} onChange={setCities} placeholder="Preferred Cities" className="mb-6" />

          <div className="flex justify-between">
            <button onClick={() => setStep(3)} className="bg-gray-200 px-4 py-2 rounded">Back</button>
            <button onClick={handleSubmit} className="bg-purple-600 text-white px-4 py-2 rounded">Submit</button>
          </div>
        </div>
      )}

      {step === 5 && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-50">
          <div className="bg-white p-10 rounded-xl text-center shadow-xl">
            <div className="text-purple-600 text-4xl mb-4">✔️</div>
            <h2 className="text-lg font-semibold mb-2">You’re Ready. Let’s Get You Hired.</h2>
            <p className="text-sm text-gray-600 mb-4">Your dashboard is ready with job matches, interview practices, and resume boosters. Take your first step today!</p>
            <button
              onClick={() => navigate('/recommended-jobs')}
              className="bg-purple-600 text-white px-6 py-2 rounded"
            >
              Explore Job Board
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
