import { FaSuitcase, FaRobot, FaFileAlt } from "react-icons/fa";
import { NavLink, useNavigate } from "react-router-dom";

export default function Sidebar({ active }) {
  const navigate = useNavigate();

  const user = JSON.parse(localStorage.getItem("user")); // Assume 'user' was saved during signup/login
  const userName = user ? `${user.firstName} ${user.lastName}` : "Guest";

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <div className="w-64 h-screen bg-white shadow-lg p-6 flex flex-col justify-between">
      <div>
        <h1 className="text-2xl font-bold text-purple-600 mb-6">
          Launch<span className="text-black">Hire</span>
        </h1>

        <nav className="space-y-4">
          <NavLink
            to="/recommended-jobs"
            className={`flex items-center gap-3 ${
              active === "recommended-jobs"
                ? "text-purple-600 font-semibold"
                : "text-gray-700"
            }`}
          >
            <FaSuitcase /> Recommend Jobs
          </NavLink>

          <NavLink
            to="/ai-interview-practice"
            className={`flex items-center gap-3 ${
              active === "interview"
                ? "text-purple-600 font-semibold"
                : "text-gray-700"
            }`}
          >
            <FaRobot /> AI Interview Practice
          </NavLink>

          <NavLink
            to="/ai-resume"
            className={`flex items-center gap-3 ${
              active === "resume"
                ? "text-purple-600 font-semibold"
                : "text-gray-700"
            }`}
          >
            <FaFileAlt /> AI Resume
          </NavLink>
        </nav>
      </div>

      <div className="mt-10 border-t pt-4 text-sm text-gray-600">
        <div className="mb-2">
          Logged in as: <br />
          <span className="font-medium text-black">{userName}</span>
        </div>
        <button
          onClick={handleLogout}
          className="text-red-500 hover:underline text-sm"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
