import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function OnboardingStep2() {
  const [selected, setSelected] = useState('');
  const navigate = useNavigate();

  const handleNext = () => {
    if (selected) {
      localStorage.setItem('onboardingStep2Choice', selected);
      navigate('/onboarding/step3'); // Replace with actual route for Step 3
    }
  };

  const handleBack = () => {
    navigate('/onboarding'); // Step 1
  };

  return (
    <div className="h-screen bg-white flex flex-col justify-start items-center p-6">
      {/* Top Logo Centered */}
      <div className="w-full text-center mt-6 mb-12">
        <h1 className="text-xl font-bold text-purple-600">
          Launch<span className="text-gray-900">Hire</span>
        </h1>
      </div>

      {/* Step content */}
      <div className="text-center max-w-lg">
        <h2 className="text-lg font-semibold mb-3 text-gray-800">
          What’s Your First Step Toward Success?
        </h2>
        <p className="text-sm text-gray-500 mb-6">
          Whether you're job hunting, practicing interviews, or polishing your resume we’ll guide you every step.
        </p>

        <div className="space-y-3">
          {['Find My First Job', 'Practice Interviews', 'Build My Resume'].map((option) => (
            <button
              key={option}
              className={`w-full px-4 py-2 rounded border ${
                selected === option
                  ? 'border-purple-600 bg-purple-50 text-purple-700 font-medium'
                  : 'border-gray-300 text-gray-700'
              }`}
              onClick={() => setSelected(option)}
            >
              {option}
            </button>
          ))}
        </div>

        {/* Navigation Buttons */}
        <div className="flex justify-center gap-4 mt-8">
          <button
            onClick={handleBack}
            className="px-6 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
          >
            Back
          </button>
          <button
            onClick={handleNext}
            className="px-6 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
            disabled={!selected}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}
