import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import onboardingImage from '../assets/onboarding-image.jpg';

export default function Onboarding() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleNext = () => {
    if (step < 7) {
      setStep((prev) => prev + 1);
    } else {
      navigate('/recommended-jobs');
    }
  };

  const handleBack = () => {
    if (step > 1) {
      setStep((prev) => prev - 1);
    }
  };

  const toggleOption = (option) => {
    setSelectedOptions((prev) =>
      prev.includes(option)
        ? prev.filter((item) => item !== option)
        : [...prev, option]
    );
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-6">
      <div className="w-full max-w-4xl bg-white p-8 rounded-lg shadow-lg text-center">
        <h1 className="text-2xl font-bold text-purple-600 mb-2">
          Launch<span className="text-black">Hire</span>
        </h1>

        {step === 1 && (
          <div className="flex flex-col md:flex-row items-center justify-center mt-6 space-y-6 md:space-y-0 md:space-x-10">
            <div className="text-left max-w-md">
              <h2 className="text-xl font-bold mb-4">Let’s Land Your First Job Faster with AI</h2>
              <p className="text-sm text-gray-600 mb-4">
                Smart job matching. AI interviews. Instant resume tailoring.
              </p>
              <button
                onClick={handleNext}
                className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
              >
                Let's Go!
              </button>
            </div>
            <img src={onboardingImage} alt="AI Assistant" className="rounded-xl w-80 h-auto" />
          </div>
        )}

        {step === 2 && (
          <div className="mt-6">
            <h2 className="text-xl font-bold mb-2">What’s Your First Step Toward Success?</h2>
            <p className="text-sm text-gray-600 mb-6">
              Whether you're job hunting, practicing interviews, or polishing your resume we’ll guide you every step.
            </p>

            <div className="flex justify-center gap-4 mb-6">
              {['Find my First Job', 'Practice Interviews', 'Build My Resume'].map((option) => (
                <label
                  key={option}
                  className="flex items-center space-x-2 border rounded px-4 py-2 cursor-pointer hover:bg-gray-100"
                >
                  <input
                    type="checkbox"
                    checked={selectedOptions.includes(option)}
                    onChange={() => toggleOption(option)}
                    className="form-checkbox"
                  />
                  <span className="text-sm">{option}</span>
                </label>
              ))}
            </div>

            <div className="flex justify-center space-x-4">
              <button
                onClick={handleBack}
                className="bg-gray-200 text-black px-4 py-2 rounded hover:bg-gray-300"
              >
                Back
              </button>
              <button
                onClick={handleNext}
                className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
              >
                Next
              </button>
            </div>
          </div>
        )}

        {/* Step 3: Education and Resume Upload */}
<div className="text-center">
  <h2 className="text-xl font-bold text-gray-800 mb-2">Help Us Know You Better.</h2>
  <p className="text-sm text-gray-500 mb-6">
    A few quick details so we can show you the most relevant jobs and best practice interviews.
  </p>

  <div className="w-full max-w-md mx-auto text-left space-y-4">
    {/* Education Status */}
    <div>
      <label htmlFor="education" className="block text-sm font-medium text-gray-700 mb-1">
        Education Status
      </label>
      <select
        id="education"
        value={formData.education}
        onChange={(e) => setFormData({ ...formData, education: e.target.value })}
        className="w-full border border-gray-300 rounded px-4 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
      >
        <option value="">Select</option>
        <option value="Undergraduate">Undergraduate</option>
        <option value="Graduate">Graduate</option>
        <option value="Postgraduate">Postgraduate</option>
        <option value="Other">Other</option>
      </select>
    </div>

    {/* Field of Study */}
    <div>
      <label htmlFor="field" className="block text-sm font-medium text-gray-700 mb-1">
        Field of Study
      </label>
      <select
        id="field"
        value={formData.field}
        onChange={(e) => setFormData({ ...formData, field: e.target.value })}
        className="w-full border border-gray-300 rounded px-4 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
      >
        <option value="">Select</option>
        <option value="Computer Science">Computer Science</option>
        <option value="Engineering">Engineering</option>
        <option value="Business">Business</option>
        <option value="Arts">Arts</option>
        <option value="Other">Other</option>
      </select>
    </div>

    {/* Skills */}
    <div>
      <label htmlFor="skills" className="block text-sm font-medium text-gray-700 mb-1">
        Skills
      </label>
      <select
        id="skills"
        value={formData.skills}
        onChange={(e) => setFormData({ ...formData, skills: e.target.value })}
        className="w-full border border-gray-300 rounded px-4 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
      >
        <option value="">Select</option>
        <option value="JavaScript">JavaScript</option>
        <option value="Python">Python</option>
        <option value="React">React</option>
        <option value="UI/UX Design">UI/UX Design</option>
        <option value="Other">Other</option>
      </select>
    </div>

    {/* Resume Upload */}
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">Upload Resume</label>
      <input
        type="file"
        accept=".pdf"
        onChange={(e) => setFormData({ ...formData, resume: e.target.files[0] })}
        className="w-full border border-dashed border-purple-400 rounded px-4 py-6 text-center cursor-pointer bg-gray-50"
      />
      <p className="text-xs text-gray-400 mt-1 text-center">Only PDF is supported (Max: 2MB)</p>
    </div>
  </div>

  {/* Navigation Buttons */}
  <div className="flex justify-center mt-6 gap-4">
    <button
      type="button"
      onClick={handleBack}
      className="bg-gray-200 hover:bg-gray-300 text-gray-700 px-6 py-2 rounded"
    >
      Back
    </button>
    <button
      type="button"
      onClick={handleNext}
      className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-2 rounded"
    >
      Next
    </button>
  </div>
</div>


        {/* Future steps can go here (3-7) */}
      </div>
    </div>
  );
}
