import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function OnboardingStep2() {
  const [selected, setSelected] = useState('');
  const navigate = useNavigate();

  const handleNext = () => {
    if (selected) {
      localStorage.setItem('onboardingStep2Choice', selected);
      navigate('/onboarding/step3'); // Replace this with your actual Step 3 route
    }
  };

  const handleBack = () => {
    navigate('/onboarding');
  };

  return (
    <div className="min-h-screen bg-white flex flex-col justify-start items-center py-12 px-6">
      {/* Logo centered at top */}
      <div className="mb-12">
        <h1 className="text-2xl font-bold text-center text-purple-600">
          Launch<span className="text-gray-900">Hire</span>
        </h1>
      </div>

      {/* Main content container */}
      <div className="w-full max-w-md text-center">
        <h2 className="text-lg font-semibold mb-2 text-gray-900">
          What’s Your First Step Toward Success?
        </h2>
        <p className="text-sm text-gray-500 mb-6">
          Whether you're job hunting, practicing interviews, or polishing your resume we’ll guide you every step.
        </p>

        {/* Option Buttons */}
        <div className="flex flex-col gap-3 mb-8">
          {['Find My First Job', 'Practice Interviews', 'Build My Resume'].map((option) => (
            <button
              key={option}
              className={`w-full border rounded px-4 py-2 transition-all duration-200 ${
                selected === option
                  ? 'border-purple-600 bg-purple-50 text-purple-700 font-medium'
                  : 'border-gray-300 text-gray-700 hover:border-purple-400'
              }`}
              onClick={() => setSelected(option)}
            >
              {option}
            </button>
          ))}
        </div>

        {/* Navigation Buttons */}
        <div className="flex justify-center gap-4">
          <button
            onClick={handleBack}
            className="px-6 py-2 bg-gray-100 text-gray-800 rounded hover:bg-gray-200"
          >
            Back
          </button>
          <button
            onClick={handleNext}
            className={`px-6 py-2 rounded text-white ${
              selected
                ? 'bg-purple-600 hover:bg-purple-700'
                : 'bg-purple-300 cursor-not-allowed'
            }`}
            disabled={!selected}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}
