import React from "react";
import Sidebar from "../components/Sidebar";

export default function AIResume() {
  const resumeData = localStorage.getItem("resumeFile");
  const resumeName = localStorage.getItem("resumeName");

  const user = JSON.parse(localStorage.getItem("user"));
  const fullName = user ? `${user.firstName} ${user.lastName}` : "Guest";

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar active="resume" />

      <main className="flex-1 p-8 overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-800">AI Resumes</h1>
          <div className="text-sm text-gray-600 font-medium">{fullName} â–¼</div>
        </div>

        {resumeData ? (
          <iframe
            src={resumeData}
            title={resumeName}
            className="w-full h-[80vh] border rounded-md shadow"
          ></iframe>
        ) : (
          <div className="text-center text-gray-600 text-lg mt-20">
            No resume uploaded.
          </div>
        )}

        <div className="mt-10">
          <input
            type="text"
            placeholder="Tell me how I can help you"
            className="w-full border px-4 py-2 rounded shadow-sm"
          />
        </div>
      </main>
    </div>
  );
}
