import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import onboardingImage from '../assets/onboarding-image.jpg';

const educationOptions = ['High School', 'Under Graduation', 'Post Graduation'];
const fieldOptions = ['Engineering', 'Business', 'Arts', 'Science'];
const skillsList = ['Python', 'Data Science', 'Django', 'Product Design'];
const jobRoles = ['Software Developer', 'Product Designer', 'Data Analyst'];
const cities = ['New York', 'Boston', 'San Francisco', 'Chicago'];
const employmentTypes = ['Remote', 'Hybrid', 'Onsite'];

export default function Onboarding() {
  const [step, setStep] = useState(1);
  const [selection, setSelection] = useState('');
  const [education, setEducation] = useState('');
  const [field, setField] = useState('');
  const [skills, setSkills] = useState([]);
  const [resume, setResume] = useState(null);
  const [jobPreferences, setJobPreferences] = useState([]);
  const [preferredCities, setPreferredCities] = useState([]);
  const [employment, setEmployment] = useState([]);

  const navigate = useNavigate();

  const toggleSelection = (value) => {
    setSelection(value);
  };

  const toggleCheckbox = (value, setter, current) => {
    setter(
      current.includes(value)
        ? current.filter((item) => item !== value)
        : [...current, value]
    );
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/pdf' && file.size <= 2 * 1024 * 1024) {
      setResume(file);
    } else {
      alert('Only PDF files under 2MB are allowed.');
    }
  };

  const handleSubmit = () => {
    // You can store this in context or send it to a backend
    navigate('/recommended-jobs');
  };

  return (
    <div className="min-h-screen bg-white flex flex-col items-center justify-center px-4">
      <h1 className="text-2xl font-bold text-center mb-4 text-purple-600">
        Launch<span className="text-gray-900">Hire</span>
      </h1>

      {step === 1 && (
        <div className="flex flex-col md:flex-row items-center gap-8 max-w-4xl">
          <img
            src={onboardingImage}
            alt="AI Guide"
            className="w-full md:w-1/2 rounded-lg shadow-md"
          />
          <div className="text-center md:text-left">
            <h2 className="text-xl font-semibold mb-2">Let’s Land Your First Job Faster with AI</h2>
            <p className="mb-4 text-sm text-gray-500">
              Smart job matching. AI interviews. Instant resume tailoring.
            </p>
            <button
              className="bg-purple-600 text-white px-6 py-2 rounded hover:bg-purple-700"
              onClick={() => setStep(2)}
            >
              Let’s Go!
            </button>
          </div>
        </div>
      )}

      {step === 2 && (
        <div className="w-full max-w-xl text-center">
          <h2 className="text-xl font-bold mb-2">What’s Your First Step Toward Success?</h2>
          <p className="text-sm text-gray-600 mb-6">
            Whether you're job hunting, practicing interviews, or polishing your resume we’ll guide you every step.
          </p>

          <div className="flex justify-center gap-4 mb-6">
            {['Find my First Job', 'Practice Interviews', 'Build My Resume'].map((opt) => (
              <label
                key={opt}
                className={`border rounded px-4 py-2 flex items-center gap-2 cursor-pointer ${
                  selection === opt ? 'bg-purple-100 border-purple-600' : 'border-gray-300'
                }`}
              >
                <input
                  type="radio"
                  checked={selection === opt}
                  onChange={() => toggleSelection(opt)}
                />
                {opt}
              </label>
            ))}
          </div>

          <div className="flex justify-center gap-4">
            <button onClick={() => setStep(1)} className="px-4 py-2 bg-gray-200 rounded">
              Back
            </button>
            <button onClick={() => setStep(3)} className="px-4 py-2 bg-purple-600 text-white rounded">
              Next
            </button>
          </div>
        </div>
      )}

      {step === 3 && (
        <div className="w-full max-w-xl">
          <h2 className="text-lg font-semibold mb-2">Help Us Know You Better.</h2>
          <p className="text-sm text-gray-500 mb-4">
            A few quick details so we can show you the most relevant jobs and best practice interviews.
          </p>

          <select
            value={education}
            onChange={(e) => setEducation(e.target.value)}
            className="w-full border p-2 rounded mb-4"
          >
            <option value="">Education Status</option>
            {educationOptions.map((e) => (
              <option key={e} value={e}>{e}</option>
            ))}
          </select>

          <select
            value={field}
            onChange={(e) => setField(e.target.value)}
            className="w-full border p-2 rounded mb-4"
          >
            <option value="">Field of Study</option>
            {fieldOptions.map((e) => (
              <option key={e} value={e}>{e}</option>
            ))}
          </select>

          <label className="block text-sm font-medium mb-1">Skills</label>
          <div className="flex flex-wrap gap-2 mb-4">
            {skillsList.map((skill) => (
              <button
                key={skill}
                className={`px-3 py-1 rounded-full border ${skills.includes(skill) ? 'bg-purple-100 border-purple-600' : 'border-gray-300'}`}
                onClick={() => toggleCheckbox(skill, setSkills, skills)}
              >
                {skill} {skills.includes(skill) && <span>&times;</span>}
              </button>
            ))}
          </div>

          <div className="mb-4 border p-4 rounded cursor-pointer text-center" onClick={() => document.getElementById('resume-upload').click()}>
            <p className="text-sm text-gray-500">Click to upload your Resume</p>
            <p className="text-xs text-gray-400">Only PDF is supported (Max : 2MB)</p>
            <input
              id="resume-upload"
              type="file"
              accept="application/pdf"
              onChange={handleFileChange}
              className="hidden"
            />
          </div>

          <div className="flex justify-between">
            <button onClick={() => setStep(2)} className="px-4 py-2 bg-gray-200 rounded">
              Back
            </button>
            <button onClick={() => setStep(4)} className="px-4 py-2 bg-purple-600 text-white rounded">
              Next
            </button>
          </div>
        </div>
      )}

      {step === 4 && (
        <div className="w-full max-w-xl">
          <h2 className="text-lg font-semibold mb-2">Dream Job? Let’s Find It Together.</h2>
          <p className="text-sm text-gray-500 mb-4">
            Choose the roles and cities you love, we’ll find the best matches for you.
          </p>

          <select
            multiple
            value={jobPreferences}
            onChange={(e) =>
              setJobPreferences(Array.from(e.target.selectedOptions, (option) => option.value))
            }
            className="w-full border p-2 rounded mb-4"
          >
            {jobRoles.map((role) => (
              <option key={role} value={role}>{role}</option>
            ))}
          </select>

          <div className="flex gap-4 mb-4">
            {employmentTypes.map((type) => (
              <label key={type} className="flex items-center gap-2">
                <input
                  type="checkbox"
                  checked={employment.includes(type)}
                  onChange={() => toggleCheckbox(type, setEmployment, employment)}
                />
                {type}
              </label>
            ))}
          </div>

          <select
            multiple
            value={preferredCities}
            onChange={(e) =>
              setPreferredCities(Array.from(e.target.selectedOptions, (option) => option.value))
            }
            className="w-full border p-2 rounded mb-4"
          >
            {cities.map((city) => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>

          <div className="flex justify-between">
            <button onClick={() => setStep(3)} className="px-4 py-2 bg-gray-200 rounded">
              Back
            </button>
            <button onClick={handleSubmit} className="px-4 py-2 bg-purple-600 text-white rounded">
              Submit
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
