import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function OnboardingStep2() {
  const [selected, setSelected] = useState('');
  const navigate = useNavigate();

  const handleNext = () => {
    if (selected) {
      localStorage.setItem('onboardingStep2Choice', selected);
      navigate('/onboarding/step3'); // Adjust this route as needed
    }
  };

  const handleBack = () => {
    navigate('/onboarding');
  };

  const options = [
    { id: 'find-job', label: 'Find my First Job' },
    { id: 'practice-interviews', label: 'Practice Interviews' },
    { id: 'build-resume', label: 'Build My Resume' },
  ];

  return (
    <div className="min-h-screen bg-white flex flex-col items-center py-12 px-4">
      {/* Logo */}
      <div className="mb-10 text-center">
        <h1 className="text-2xl font-bold text-purple-600">
          Launch<span className="text-gray-900">Hire</span>
        </h1>
      </div>

      {/* Content */}
      <div className="w-full max-w-lg text-center">
        <h2 className="text-xl font-semibold text-gray-900 mb-2">
          What’s Your First Step Toward Success?
        </h2>
        <p className="text-sm text-gray-500 mb-8">
          Whether you're job hunting, practicing interviews, or polishing your resume we’ll guide you every step.
        </p>

        {/* Custom Styled Checkboxes */}
        <div className="flex justify-center gap-4 mb-10">
          {options.map((option) => (
            <label
              key={option.id}
              className={`flex items-center gap-2 px-4 py-3 border rounded-lg cursor-pointer text-sm ${
                selected === option.id
                  ? 'border-purple-600 bg-purple-50 text-purple-700 font-medium'
                  : 'border-gray-300 text-gray-600 hover:border-purple-400'
              }`}
            >
              <input
                type="radio"
                name="step2option"
                value={option.id}
                checked={selected === option.id}
                onChange={() => setSelected(option.id)}
                className="form-radio text-purple-600"
              />
              {option.label}
            </label>
          ))}
        </div>

        {/* Buttons */}
        <div className="flex justify-center gap-4">
          <button
            onClick={handleBack}
            className="px-6 py-2 bg-gray-100 text-gray-800 rounded hover:bg-gray-200"
          >
            Back
          </button>
          <button
            onClick={handleNext}
            disabled={!selected}
            className={`px-6 py-2 rounded text-white ${
              selected ? 'bg-purple-600 hover:bg-purple-700' : 'bg-purple-300 cursor-not-allowed'
            }`}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}
